import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './AllReplies.css';

const AllReplies = () => {
  const { id } = useParams();
  const [replies, setReplies] = useState([]);

  useEffect(() => {
    const fetchAllReplies = async () => {
      try {
        const response = await fetch(`http://localhost:5000/teachchat?Teacher_id=${id}`);
        const data = await response.json();
        if (data && data.length > 0) {
          setReplies(data);
        } else {
          alert('No replies found.');
        }
       
      } catch (error) {
        console.error('Error fetching replies:', error);
        alert('Error fetching data');
      }
    };

    fetchAllReplies();
    for(let i=0;i<replies;i++){
        fetchpremessages(replies.Replyid)
    }
  }, [id]);

  return (
    <div className="all-replies-container">
      <h1>All Replies</h1>
      <div className="replies-list">
        {replies.map((reply, index) => (
          <div key={index} className="reply-item">
            <div className="reply-header">
              <p><strong>Reply ID:</strong> {reply.Replyid}</p>
              <p><strong>Date:</strong> {new Date(reply.Date).toLocaleDateString()}</p>
            </div>
            <div className="reply-content">
              <p>{reply.Message}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AllReplies;
