import React, { useState } from 'react';
import './Message_C.css';

const Message_C = () => {
  const [message, setMessage] = useState('');
  const [selectedClass, setSelectedClass] = useState('');
  const [feedback, setFeedback] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (selectedClass === '' || message === '') {
      alert('Please select a class and write a message.');
      return;
    }
 
    try {
      const response = await fetch('http://localhost:5000/teacher_meassage', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ClassNumber: selectedClass,
          Message: message,
        }),
      });

      const data = await response.json();

      if (data.success) {
        setFeedback('Message added successfully!');
      } else {
        setFeedback('There was an error adding the message.');
      }
      
      // Clear the form after submission
      setSelectedClass('');
      setMessage('');
    } catch (error) {
      console.log('Error in adding message:', error);
      setFeedback('There was an error adding the message.');
    }
  };

  return (
    <div className="message-container">
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="class">Class:</label>
          <select
            id="class"
            value={selectedClass}
            onChange={(e) => setSelectedClass(e.target.value)}
          >
            <option value="">Select a class</option>
            <option value="1">Class 1</option>
            <option value="2">Class 2</option>
            <option value="3">Class 3</option>
            <option value="4">Class 4</option>
            <option value="5">Class 5</option>
            <option value="6">Class 6</option>
            <option value="7">Class 7</option>
            <option value="8">Class 8</option>
            <option value="9">Class 9</option>
            <option value="10">Class 10</option>
          </select>
        </div>
        <div>
          <label htmlFor="message">Message:</label>
          <textarea
            id="message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
        </div>
        <div>
          <button type="submit">Submit</button>
        </div>
      </form>
      {feedback && <p>{feedback}</p>}
    </div>
  );
};

export default Message_C;
