import React, { useState, useEffect } from 'react';
import './Navbar.css';
import logo from '../../assets/nav-logo.svg';
import homebutton from '../../assets/homebutton.png';
import { Link } from 'react-router-dom';
import addproduct_icon from '../../assets/Product_Cart.svg';
import listproduct_icon from '../../assets/Product_list_icon.svg';

const Navbar = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [teacherdata, setTeacherData] = useState([]);
  const handleLogout = () => {
    localStorage.removeItem('key');
    window.location.replace('/');
    
};

  useEffect(() => {
    const fetchInfo = async () => {
      const id = localStorage.getItem('id'); // Assuming phoneno is stored in localStorage
      if (id) {
        const response = await fetch(`http://localhost:5000/classdata?id=${id}`);
        const data = await response.json();
        setTeacherData(data);
      alert(data);
      }
    };
    fetchInfo();
  }, []);

  const handleSidebarToggle = () => {
    setSidebarOpen(!sidebarOpen);
  };

  return (
    <div className='navbar'>
        <div className="navbar-item" >
                    <img src={logo} alt="Logo" className="logo" />
                </div>
                <div className="navbar-item profile">
                    {teacherdata.map((product, index) => (
                        <div key={index} className='list-format-main listproduct-format'>
                            <p>{product.name}</p>
                            <img src={product.image} alt=" " className='listproduct-image' />
                        </div>
                    ))}
                </div>
                <div className="navbar-item logout">
                    <button onClick={handleLogout} className="logout-button">Logout</button>
                </div>
            
      
      <div className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
        <Link to='/listproduct' className='Sidebar-item'>
          <img src={listproduct_icon} alt="List Icon" />
          <p>Show Student</p>
        </Link>
        <Link to='/createform' className='Sidebar-item'>
          <img src={addproduct_icon} alt="Add Product Icon" />
          <p>Create Test</p>
        </Link>
        <div className='Sidebar-item' onClick={handleSidebarToggle}>
          <img src={listproduct_icon} alt="Close Icon" />
          <p>Close</p>
        </div>
      </div>
    </div>
  );
};

export default Navbar;
