import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
// import './MessageDetails.css';

const ReceivedMessage = () => {
  const [message, setMessage] = useState('');
  const [date, setDate] = useState('');
  const [Replyid, setReplyid] = useState(null);
  const [premessage, setPremessage] = useState('');
  const { id } = useParams();

  useEffect(() => {
    const fetchMessageDetails = async () => {
      try {
        const response = await fetch(`http://localhost:5000/teachchat?Teacher_id=${id}`);
        const data = await response.json();

        if (data && data.length > 0) {
          
          setMessage(data[data.length - 1]);
           // Make sure the API response includes TeacherName
          setReplyid(data[data.length-1].Replyid);
          setDate(new Date(data[data.length-1].Date).toLocaleDateString());
          alert(Replyid);
          // Fetch the original message if Replyid is not 0
          if (data[data.length-1].Replyid !== 0) {
            const response = await fetch(`http://localhost:5000/replychat?Replyid=${data[data.length-1].Replyid}`);
            const replyData = await response.json();
            if (replyData && replyData.length > 0) {
              setPremessage(replyData[replyData.length - 1].Message);
            }
          }
        }
      } catch (error) {
        console.error('Error fetching message details:', error);
      }
    };

    fetchMessageDetails();
  }, [id]);

  return (
    <div className="message-details-container">
      <div className="message-header">
        <h2>Replied Message</h2>
        {premessage && <p>Previous Message: {premessage}</p>}
        <p>{date}</p>
      </div>
      <div className="message-content">
        <p>{message}</p>
      </div>
    </div>
  );
};

export default ReceivedMessage;
