import React, { useState, useEffect } from 'react';
import './Navbar.css';
import logo from '../../assets/nav-logo.svg';
import homebutton from '../../assets/homebutton.png';
import { Link } from 'react-router-dom';
import addproduct_icon from '../../assets/Product_Cart.svg';
import listproduct_icon from '../../assets/Product_list_icon.svg';

const Navbar = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [studentdata, setStudentData] = useState([]);
  const RollNo=localStorage.getItem('user');
  const handleLogout = () => {
    localStorage.removeItem('key');
    window.location.replace('/');
    
};

const fetchinfowithparameter = async (RollNo) => {
    try {
        const response = await fetch(`http://localhost:5000/studentdata?RollNo=${RollNo}`);
        const data = await response.json();
        setStudentData(data);
        localStorage.setItem('classNumber',data[0].classNumber)
    } catch (error) {
        console.error(error);
    }
};

useEffect(() => {
    if (RollNo !== undefined) {
        fetchinfowithparameter(RollNo);
    }
}, [RollNo]);

  const handleSidebarToggle = () => {
    setSidebarOpen(!sidebarOpen);
  };

  return (
    <div className='navbar'>
        <div className="navbar-item" >
                    <img src={logo} alt="Logo" className="logo" onClick={handleSidebarToggle} />
                </div>
                <div className="navbar-item profile">
                    {studentdata.map((product, index) => (
                        <div key={index} className='list-format-main listproduct-format'>
                            <p>{product.name}</p>
                            <img src={product.image} alt=" " className='listproduct-image' />
                        </div>
                    ))}
                </div>
                <div className="navbar-item logout">
                    <button onClick={handleLogout} className="logout-button">Logout</button>
                </div>
            
      
      <div className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
        {/* <Link to='/examresult' className='Sidebar-item'>
          <img src={listproduct_icon} alt="List Icon" />
          <p>ExamResult</p>
        </Link> */}
       
      </div>
    </div>
  );
};

export default Navbar;
