        <p>Student name</p>
        <p>RollNo</p>
        <p>Phoneno</p>
        <p>Remove</p>
      </div>
      <div className="lsitproduct-allproduct">
        <hr/>
        {classStudents.map((product,index)=>{
            return <div key={index} className='list-format-main listproduct-format'>
              
              <p>{product.name}</p>
              <p>{product.RollNo}</p>
              <p>{product.Phoneno}</p>
              {/* <p>{product.class}</p> */}
              
            </div>
          

      
        })}
      </div>
    </div>
    </div>
  )
}

export default Testinput
