import React, { useState, createContext } from 'react';
import './CreateForm.css';

// Create a context
export const ClassContext = createContext();

const CreateForm = () => {
    const [subject, setSubject] = useState('');
    const [className, setClassName] = useState('');
    const [totalMarks, setTotalMarks] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        // Handle form submission here

        if (subject === '' || className === '' || totalMarks === '') {
            window.location.replace('/createform');
        } else {
            // Pass className and totalMarks to the context
            // window.alert(className);
           
            window.location.replace(`/testinput/${className}/${totalMarks}/${subject}`);
        }
    };

    return (
        // Provide the context value to the context provider
        <ClassContext.Provider value={{ className, totalMarks }}>
            <div className="createform">
                <form onSubmit={handleSubmit}>
                    <div>
                        <label>
                            Subject:
                           
                        </label>
                    </div>
                    <div>
                        <label>
                            Class:
                            <select value={className} onChange={(e) => setClassName(e.target.value)}>
                                <option value="">Select a class</option>
                                <option value="1">Class 1</option>
                                <option value="2">Class 2</option>
                                <option value="3">Class 3</option>
                                <option value="4">Class 4</option>
                                <option value="5">Class 5</option>
                                <option value="6">Class 6</option>
                                <option value="7">Class 7</option>
                                <option value="8">Class 8</option>
                                <option value="9">Class 9</option>
                                <option value="10">Class 10</option>
                            </select>
                        </label>
                    </div>
                    <div>
                        <label>
                            Total Marks:
                            <input
                                type="number"
                                value={totalMarks}
                                onChange={(e) => setTotalMarks(e.target.value)}
                            />
                        </label>
                    </div>
                    <div>
                        <button type="submit">Create</button>
                    </div>
                </form>
            </div>
        </ClassContext.Provider>
    );
}

export default CreateForm;
