import React, { useState, useEffect } from 'react';
import './Teacherlogin.css';

function Teacherlogin() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const [loginData, setLoginData] = useState([]);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/alldatateacher');
      const data = await response.json();
      setLoginData(data);

      const user = data.find(
        (user) => user.name === username && user.phoneno === password 
        
      );
      
      if (user) {
        
        console.log('Login successful:', username);
        localStorage.setItem('key', '120'); // Set key as a string
        localStorage.setItem('user', username);
        localStorage.setItem('id', user.id);
        window.location.replace('/admin');
        setError('');
        // Perform further actions on successful login
      } else {
        setError('Invalid username or password');
      }
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Error fetching login data');
    }
  };

  return (
    <div className="login-container">
     
      <div className="login-form">
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Username"
          />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
          />
          <button type="submit">Login</button>
        </form>
        {error && <p className="error">{error}</p>}
      </div>
    </div>
  );
}

export default Teacherlogin;
