import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './Testinput.css';

const Testinput = () => {
  const { className, totalMarks,subject } = useParams();
  const [testdata, setTestdata] = useState([]);
  const [inputValues, setInputValues] = useState({});

  const fetchInfo = async (className) => {
    try {
      const response = await fetch(`http://localhost:5000/classdata?classNumber=${className}`);
      const data = await response.json();

      if (Array.isArray(data)) {
        setTestdata(data);
      } else {
        console.error("Data fetched is not an array:", data);
        setTestdata([]);
      }
    } catch (error) {
      console.log("Error in fetching data", error);
    }
  };

  useEffect(() => {
    fetchInfo(className);
  }, [className]);

  const handleChange = (e, id) => {
    const { value } = e.target;
    setInputValues({ ...inputValues, [id]: value });
    console.log(inputValues);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:5000/updateTestdata`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ className, inputValues }),
      });

      if (response.ok) {
        const data = await response.json();
        console.log("Data updated successfully", data);
        fetchInfo(className); // Re-fetch data to reflect updates
      } else {
        console.error("Failed to update data");
      }
    } catch (error) {
      console.error("Error in submitting data", error);
    }
  };

  return (
    <div className='student-container'>
      <div className="list-product">
        <h2>All Student List of {className}</h2>
        <form onSubmit={handleSubmit}>
          <div className="list-format-main">
            <p>Student Name</p>
            <p>Roll No</p>
            <p>Total Marks</p>
            <p>Marks</p>
          </div>
          <div className="listproduct-allproduct">
            <hr />
            {testdata.map((product, index) => (
              <div key={index} className='list-format-main listproduct-format'>
                <p>{product.name}</p>
                <p>{product.RollNo}</p>
                <p>{totalMarks}</p>
                <input
                  type='text'
                  value={inputValues[product.RollNo] || ''}
                  onChange={(e) => handleChange(e, product.RollNo)}
                />
              </div>
            ))}
          </div>
          <button type="submit">Submit</button>
        </form>
      </div>
    </div>
  );
};

export default Testinput;
