import React, { useState } from 'react';
import './Navbar.css';
import navlogo from '../../assets/nav-logo.svg';
import navprofile from '../../assets/nav-profile.svg';
import homebutton from '../../assets/homebutton.png';
import { Link } from 'react-router-dom';
import addproduct_icon from'../../assets/Product_Cart.svg';
import listproduct_icon from '../../assets/Product_list_icon.svg';

const Navbar = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const[teacherdata,setteacherdata]=useState([]);

  const handleSidebarToggle = () => {
    setSidebarOpen(!sidebarOpen);
  };
  const fetchinfo=async(phoneno)=>{
    const response=await fetch(`http://localhost:5000/teacherpdata?phoneno=${phoneno}`);
    const data= await response.json();
    setteacherdata(data);

  }

  return (
    <div className='navbar'>
      <img
        className="buttonclass"
        src={homebutton}
        alt="Home Button"
        onClick={handleSidebarToggle}
      />
      {teacherdata.map((teacher,index))

      }
      <img src={navlogo} alt="Logo" />
      <img src={navprofile} alt="Profile" />
      <div className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
        <Link to ={'/listproduct'} style={{textDecoration:"none"}}>
          <div className='Sidebar-item'>
              <img src={listproduct_icon} alt=""></img>
              <p>Show Student</p>
          </div>
        </Link>
        <Link to ={'/createform'} style={{textDecoration:"none"}}>
          <div className='Sidebar-item'>
              <img src={addproduct_icon} alt=""></img>
              <p>Create Test</p>
          </div>
        </Link>
        <div className='Sidebar-item' onClick={handleSidebarToggle}>
          <img src={listproduct_icon} alt=""></img>
          <p>Close</p>
        </div>
      </div>
    </div>
  );
};

export default Navbar;
