import React, { useState, useEffect } from 'react';
import './Navbar.css';
import logo from '../../assets/nav-logo.svg';
import { Link } from 'react-router-dom';
import addproduct_icon from '../../assets/Product_Cart.svg';
import listproduct_icon from '../../assets/Product_list_icon.svg';

const Navbar = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [userData, setUserData] = useState([]);
  const userChoice = localStorage.getItem('userChoice');
  const RollNo = localStorage.getItem('user');

  const handleLogout = () => {
    localStorage.removeItem('key');
    window.location.replace('/');
  };

  const fetchUserData = async (userChoice, RollNo) => {
    try {
      let endpoint = '';
      if (userChoice === 'teacher') {
        endpoint = `http://localhost:5000/t_data?id=${RollNo}`;
      } else if (userChoice === 'student') {
        endpoint = `http://localhost:5000/studentdata?RollNo=${RollNo}`;
      }

      const response = await fetch(endpoint);
      if (!response.ok) {
        throw new Error('Failed to fetch user data');
      }
      
      const data = await response.json();
      setUserData(data);
      
      if (userChoice === 'student') {
        localStorage.setItem('classNumber', data[0].classNumber);
      }
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };

  useEffect(() => {
    if (userChoice && RollNo) {
      fetchUserData(userChoice, RollNo);
    }
  }, [userChoice, RollNo]);

  const handleSidebarToggle = () => {
    setSidebarOpen(!sidebarOpen);
  };

  return (
    <div className='navbar'>
      <div className="navbar-item">
        <img src={logo} alt="Logo" className="logo" onClick={handleSidebarToggle} />
      </div>
      <div className="navbar-item profile">
        {userData.map((user, index) => (
          <div key={index} className='list-format-main listproduct-format'>
            <p>{user.name}</p>
            <img src={user.image} alt={user.name} className='listproduct-image' />
          </div>
        ))}
      </div>
      <div className="navbar-item logout">
        <button onClick={handleLogout} className="logout-button">Logout</button>
      </div>

      <div className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
        {userChoice === 'teacher' && (
          <>
            <Link to='/listproduct' className='Sidebar-item'>
              <img src={listproduct_icon} alt="List Icon" />
              <p>Show Students</p>
            </Link>
            <Link to='/createform' className='Sidebar-item'>
              <img src={addproduct_icon} alt="Add Product Icon" />
              <p>Create Test</p>
            </Link>
          </>
        )}
        {userChoice === 'student' && (
          <p onClick={win}> </p>
        )}
        <div className='Sidebar-item' onClick={handleSidebarToggle}>
          <img src={listproduct_icon} alt="Close Icon" />
          <p>Close</p>
        </div>
      </div>
    </div>
  );
};

export default Navbar;
