import React, { useState } from 'react';
// import './MessageComponent.css';
import { useParams } from 'react-router-dom';


const OpenMessage = () => {
    const [message,setMessage]=useState('');
    const[Teacher_id,setTeacher_id]=useState();
    const[Student_id,setStudent_id]=useState();
    const {id}=useParams();
    useEffect(() => {
        const fetchMessages = async () => {
          
          try {
            const response = await fetch(`http://localhost:5000/studentchat?id=${id}`);
            const data = await response.json();
           
            if (data && data.length > 0 ) {
              setMessage(data[data.length-1].Message);
              setTeacher_id(data[data.length-1].Teacher_id);
              setStudent_id(data[data.length-1].Student_id);

            }
          } catch (error) {
            console.error('Error fetching messages:', error);
          }
        };
    
        fetchMessages();
      }, []);
      const replychat={
        Student_id:Student_id,
        Teacher_id:Teacher_id,
        Message:message,
      }
  return (
    <div className="message-container">
      <div className="message">
        {message}
      </div>
      <textarea className="message-input" placeholder="Type your response here..."></textarea>
    </div>
  );
}

export default OpenMessage;
