import React, { useState, createContext } from 'react';
import './CreateForm.css';

// Create a context
export const ClassContext = createContext();

const CreateForm = () => {
    const [subject, setSubject] = useState('');
    const [className, setClassName] = useState('');
    const [totalMarks, setTotalMarks] = useState('');
    useEffect(() => {
        const fetchInfo = async () => {
          const id = localStorage.getItem('id'); // Assuming phoneno is stored in localStorage
          if (id) {
            const response = await fetch(`http://localhost:5000/t_data?id=${id}`);
            const data = await response.json();
            setTeacherData(data);
          //alert(data);
          }
        };
        fetchInfo();
      }, []);

    const handleSubmit = (e) => {
        e.preventDefault();
        // Handle form submission here

        if (subject === '' || className === '' || totalMarks === '') {
            window.location.replace('/createform');
        } else {
            // Pass className and totalMarks to the context
            // window.alert(className);
           
            window.location.replace(`/testinput/${className}/${totalMarks}/${subject}`);
        }
    };

    return (
        // Provide the context value to the context provider
        <ClassContext.Provider value={{ className, totalMarks }}>
            <div className="createform">
                <form onSubmit={handleSubmit}>
                    <div>
                        <label>
                            Subject:
                            <select value={subject} onChange={(e) => setSubject(e.target.value)}>
                                <option value="">Select a subject</option>
                                <option value="Math">Math</option>
                                <option value="Science">Science</option>
                                <option value="History">History</option>
                                <option value="English">English</option>
                                <option value="Hindi">English</option>
                            </select>
                        </label>
                    </div>
                    <div>
                        <label>
                            Class:
                           <p> {localStorage.getItem('')}</p>
                        </label>
                    </div>
                    <div>
                        <label>
                            Total Marks:
                            <input
                                type="number"
                                value={totalMarks}
                                onChange={(e) => setTotalMarks(e.target.value)}
                            />
                        </label>
                    </div>
                    <div>
                        <button type="submit">Create</button>
                    </div>
                </form>
            </div>
        </ClassContext.Provider>
    );
}

export default CreateForm;
