import React, { useState, useEffect } from 'react';
import './ExamResult.css';

const ExamResult = () => {
  const [examResults, setExamResults] = useState([]);

  useEffect(() => {
    const fetchExamResults = async () => {
      const RollNo=localStorage.getItem('user');
      try {
        const response = await fetch(`http://localhost:5000/examstudent?RollNo=${RollNo}`);
        const data = await response.json();
        setExamResults(data);
      } catch (error) {
        console.error('Error fetching exam results:', error);
      }
    };

    fetchExamResults();
  }, []);

  return (
    <div className="exam-result">
      <h1>Exam Results</h1>
      {examResults.map((result, index) => (
        <div key={index} className="marksheet">
          <div className="student-info">
            <p><strong>Name:</strong> {result.name}</p>
            <p><strong>Roll No:</strong> {result.RollNo}</p>
            <p><strong>Class:</strong> {result.class}</p>
          </div>
          <table className="marks-table">
            <thead>
              <tr>
                <th>Subject</th>
                <th>Marks</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>English</td>
                <td>{result.Subjects.English}</td>
              </tr>
              <tr>
                <td>Hindi</td>
                <td>{result.Subjects.Hindi}</td>
              </tr>
              <tr>
                <td>Subject 3</td>
                <td>{result.Subjects.Socialscience}</td>
              </tr>
              <tr>
                <td>Subject 4</td>
                <td>{result.Subjects.Math}</td>
              </tr>
              <tr>
                <td>Subject 5</td>
                <td>{result.Subjects.Science}</td>
              </tr>
              <tr>
                <td>Subject 6</td>
                <td>{result.Subjects.subject6}</td>
              </tr>
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
};

export default ExamResult;
