// OTPForm.jsx
import React, { useState } from 'react';
import { RecaptchaVerifier, signInWithPhoneNumber } from 'firebase/auth';
import { auth } from './firebaseConfig';

const OTPForm = () => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [otp, setOtp] = useState('');
  const [confirmationResult, setConfirmationResult] = useState(null);

  

  const handleSendOtp = async () => {

    try {
      const recapcha = new RecaptchaVerifier('recaptcha-container', {
        'size': 'normal',
        'callback': (response) => {
          // reCAPTCHA solved, allow signInWithPhoneNumber.
          handleSendOtp();
        }
      }, auth);
      const phoneNumberString = '+91' + phoneNumber; // Add your country code
      const confirmation = await signInWithPhoneNumber(auth, phoneNumberString, appVerifier);
      setConfirmationResult(confirmation);
      alert('OTP sent');
    } catch (error) {
      console.log('Error in OTP sending', error);
      alert('Failed to send OTP');
    }
  };

  const handleVerifyOtp = async () => {
    if (!confirmationResult) {
      alert('Please request OTP first');
      return;
    }

    try {
      const result = await confirmationResult.confirm(otp);
      console.log('User signed in successfully:', result.user);
      alert('Phone number verified successfully');
    } catch (error) {
      console.error('Error during OTP verification', error);
      alert('Failed to verify OTP');
    }
  };

  return (
    <div className="phone-verification-container">
      <h1>Phone Number Verification</h1>
      <div>
        <input
          type="text"
          value={phoneNumber}
          onChange={(e) => setPhoneNumber(e.target.value)}
          placeholder="Enter phone number"
        />
        <button onClick={handleSendOtp}>Send OTP</button>
      </div>
      <div>
        <input
          type="text"
          value={otp}
          onChange={(e) => setOtp(e.target.value)}
          placeholder="Enter OTP"
        />
        <button onClick={handleVerifyOtp}>Verify OTP</button>
      </div>
      <div id="recaptcha-container"></div>
    </div>
  );
};

export default OTPForm;
