import React, { useState, useEffect } from 'react';
import './LoginPage.css';

function LoginPage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loginData, setLoginData] = useState([]);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/alldata');
      const data = await response.json();
      setLoginData(data);

      const user = data.find(
        (user) => user.RollNo === username && user.Phoneno === password
      );
      
      if (user) {
        
        console.log('Login successful:', username);
        localStorage.setItem('key', '123'); // Set key as a string
        localStorage.setItem('user', username);
        window.location.replace('/admin');
        setError('');
        // Perform further actions on successful login
      } else {
        setError('Invalid username or password');
      }
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Error fetching login data');
    }
  };

  return (
    <div className="login-container">
     
      <div className="login-form">
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Username"
          />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
          />
          <button type="submit">Login</button>
        </form>
        {error && <p className="error">{error}</p>}
      </div>
    </div>
  );
}

export default LoginPage;
